x1_step1.ymin = -1;

% Input 2
x2_step1.xoffset = 0;
x2_step1.gain = 1.99292511583877;
x2_step1.ymin = -1;

% Layer 1
b1 = [-3.0533582155311421;-1.4295487200968711;-4.9131625640409808;4.1242665132777718;-0.92942223134464508];
IW1_1 = [-0.0841615816999981 -1.1686123574484124 -0.16662971649283298 -0.14207248881923459 0.09233899812077008 0.23664589047102247 0.31440501417636513 0.33641924148802294 1.4783927702303894 -0.57635160958883436 -3.7575581033750241 0.62103969056354325;0.046546167326880404 -1.097306960267505 0.6830512179466155 -0.22769368340660201 -0.62589267842350749 -0.055669553209969393 -0.53463965173923478 -0.43451572151510093 -2.8750342878518862 1.0089908541956993 5.4052227857141695 -0.7371795010812473;-0.010941015635593541 -1.3751354857777549 -2.1241923886372249 0.035302613799830733 -0.07437512933395217 -0.023156333870091354 -0.15520969870201937 -0.013932932495481025 0.37121802535978654 -0.43327991959593765 -2.160856774027716 1.0485872896890738;0.031212973647237578 3.0910289141038136 0.36869546102557482 0.05230295972706904 0.051859589982352808 0.12640375508997898 -0.028051927295280412 0.064408285016271036 0.92657278943333465 -0.38002865009108916 -1.6958281408752989 0.17811475020030182;0.046931776054540672 -1.0664224026317179 0.68364516828046162 -0.22760641581966271 -0.62536186615429856 -0.052764760304787717 -0.5275753081746819 -0.42810557509330871 -2.8720626688049982 1.0032867940647949 5.3969733433340323 -0.74138099449585593];
IW1_2 = [1.236989372360892;1.5289392446416539;0.9802608422232566;-1.374779210934425;2.0181645239996349];

