% Input 2
x2_step1.xoffset = 0;
x2_step1.gain = 1.99292511583877;
x2_step1.ymin = -1;

% Layer 1
b1 = [-8.7252951755753934;3.517500057016739;2.4574717557348915;-2.0088554555273181;2.1876809575741794];
IW1_1 = [0.074418471976438416 0.77905314918505797 1.2804807291611211 -0.096775216421555024 -0.90170878972013946 -0.020079494805554994 -1.5767205554809329 -0.9473742007785978 -4.5592911385308552 1.5011581040194395 7.2037453175225679 -1.3169850315961096;0.016139320053529894 1.8042740488672053 1.0675245355521321 -0.028929609942939159 -0.047666408649813342 0.17096325178447574 0.12743656940131509 0.10123241080080861 0.46105545669385023 -0.26982097580773134 -0.71424927394214333 0.12164955333203752;0.026700179250106246 1.1452648821748674 1.3707482153366248 -0.0011738509789651795 0.063869996914836732 -0.018069061914931475 0.041338510014278337 -0.040620409497696866 -0.33468230143197242 0.39711055075668322 1.9883964675332357 -1.1486235179479165;-0.068354533998143188 -0.85289657619130466 -1.322677618153792 0.11996923970474262 0.83014187504563841 0.031737642354467674 1.0962173668263295 0.71471749592807676 4.5622794914182725 -1.4748253532287248 -7.4902937455996517 1.4172567388350203;0.025716750149441383 0.94746037882050893 1.0658599308242385 0.0060976753238014192 0.050604143705557263 -0.026174853018098281 0.017570293907258269 -0.050731791190795239 -0.33034841622680461 0.35189181149804177 1.7338882292454632 -0.94799510309193646];
IW1_2 = [-6.6603966853832928;-1.2439119423015059;-0.51449350056778054;-4.2220693840590204;-0.50871334556509629];

