x1_step1.ymin = -1;

% Input 2
x2_step1.xoffset = 0;
x2_step1.gain = 1.99292511583877;
x2_step1.ymin = -1;

% Layer 1
b1 = [30.21401914879192;-5.4863923232934866;0.59520342095154688;-2.4498980078427381;8.2474244205565874];
IW1_1 = [0.095747241712469455 17.828739446986862 10.760934664294274 0.010334797902199081 -0.11286735202686812 0.43047707452984274 -0.48505914304990089 -0.29158469377967972 1.7579836781167948 -2.408164645370511 -0.51769835828299515 0.83927805322454274;0.011435254248885491 -1.7840638840507228 -2.2189882288425302 -0.011753018341747346 -0.0034775405312309545 -0.068493231305055746 0.026319893692447728 0.13985937951184119 0.58880236268332087 -3.6031310483279513 -0.45767236745251322 1.9506238524113253;-0.14360624074689429 0.031953805780588491 0.45533097688959723 -0.14020026089753529 0.02607359523296119 0.29200907386606556 0.11244061062668746 0.11813275941046837 0.4332466697868152 -0.57904288717611085 -0.14991120951295689 0.013397731453217852;-0.00285474294412858 -0.67296365313112916 -1.4830980027258271 -0.14278028660801609 0.32089792254403771 0.059947275188152967 0.4482775748344458 0.47052973904550377 1.1917916250913438 -4.8555242771532567 -0.16775607893709649 1.4166093624987852;0.034593735320735099 -0.848411445511285 -1.7907880462158274 -0.19362701434071303 0.26608766825861002 0.15955178897172156 0.47024524946088331 0.41242055842434311 1.2919282781080887 -4.6902400822107477 -0.20180789872809196 1.4195712753328984];
IW1_2 = [-1.3905556981822249;0.48267133780401;1.9126362831322601;-3.0856263092997658;8.1557164208664403];

