% Input 2
x2_step1.xoffset = 0;
x2_step1.gain = 1.99292511583877;
x2_step1.ymin = -1;

% Layer 1
b1 = [-8.7935493104825913;-4.5368252657074279;6.6662544295696504;-0.4860377798392333;-1.6645906108456658];
IW1_1 = [-0.47164284312930921 0.92164781582489042 0.021126106883006007 -0.78681658839081925 0.076866343325525333 -1.3056674512903332 -0.87164621296251221 -3.8745030779751382 2.0776633923108618 6.3976596578931337 -0.97489066822460746 -1.0244628734155585;-1.0396489027527593 -0.67006922325852658 0.055409285188717232 -0.098808122090285458 0.031187947891564954 0.024961721050222943 0.049283957995767479 0.37329265701311143 -4.5867983211679499 -4.3407929315432767 4.9247927292832472 0.51287347571742148;3.9634701351885013 2.1215410741640541 0.0064148790856932434 -0.00061411215728935124 0.09463506942583276 0.08273988030254345 0.066654114315748081 0.40317169223409377 -0.45575703003163642 -0.6392513086767927 0.2795725201743583 0.096668977806503797;0.52774903378492133 -0.99962241836888854 0.064154087224078837 0.85851380609203409 0.010677880662847389 1.0728096276645063 0.73312316768105856 4.5309807723785953 -2.7713409185476809 -7.7935771341349227 1.5589699322006032 1.2244764590011403;2.1107452964924285 2.633351900261645 -0.030677980238726716 0.068353514947072105 0.94522956863259322 -0.62372988330861268 -0.2782845093881543 -0.60886931461927618 0.19919684722680686 1.3055403953241078 0.33292345442786603 -0.28099578178671181];
IW1_2 = [-5.6614599413055329;0.74191112841317897;-1.1041031951229621;-4.3171189045402372;-5.0537778875912487];

